import React, { useEffect } from "react";
import { useListsContext } from "../hooks/useListContext";
import { useAuthContext } from "../hooks/useAuthContext"; // Import auth context
import { useNavigate } from "react-router-dom";

const Lists = () => {
  const { lists, dispatch } = useListsContext();
  const { user } = useAuthContext(); // Get user from auth context
  const navigate = useNavigate();

  useEffect(() => {
    const fetchLists = async () => {
      if (!user) return; // Ensure user is logged in

      const response = await fetch("/api/lists", {
        headers: {
          Authorization: `Bearer ${user.token}`, // Include token
        },
      });

      const data = await response.json();
      if (response.ok) {
        dispatch({ type: "SET_LISTS", payload: data });
      }
    };

    fetchLists();
  }, [dispatch, user]);

  const handleDelete = async (id) => {
    if (!user) return;

    const response = await fetch(`/api/lists/${id}`, {
      method: "DELETE",
      headers: {
        Authorization: `Bearer ${user.token}`, // Include token
      },
    });

    if (response.ok) {
      dispatch({ type: "DELETE_LIST", payload: id });
    }
  };

  return (
    <div>
      <h2>Saved Lists</h2>
      {lists.map((list) => (
        <div
          key={list._id}
          style={{ borderBottom: "1px solid gray", padding: "10px" }}
        >
          <h3>{list.name}</h3>
          <p>Codes: {list.codes.join(", ")}</p>
          <button onClick={() => navigate(`/lists/${list._id}`)}>Edit</button>
          <button onClick={() => handleDelete(list._id)}>Delete</button>
        </div>
      ))}
    </div>
  );
};

export default Lists;
