import React, { useState } from "react";
import Card from "./Card";
import styled from "styled-components";

const httpStatusCodes = [
  100, 101, 102, 103, 200, 201, 202, 203, 204, 205, 206, 207, 208, 226, 300,
  301, 302, 303, 304, 305, 307, 308, 400, 401, 402, 403, 404, 405, 406, 407,
  408, 409, 410, 411, 412, 413, 414, 415, 416, 417, 418, 421, 422, 423, 424,
  425, 426, 428, 429, 431, 451, 500, 501, 502, 503, 504, 505, 506, 507, 508,
  510, 511,
];

const Container = styled.div`
  text-align: center;
  padding: 20px;
`;

const SearchInput = styled.input`
  padding: 10px;
  width: 250px;
  margin-bottom: 20px;
  font-size: 16px;
`;

const ImageGrid = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 10px;
  justify-content: center;
`;

const Home = () => {
  const [search, setSearch] = useState("");

  const filterStatusCodes = (query) => {
    if (!query) return httpStatusCodes;
    const regex = new RegExp(`^${query.replace(/x/g, "\\d")}$`);
    return httpStatusCodes.filter((code) => regex.test(code.toString()));
  };

  const filteredCodes = filterStatusCodes(search);

  return (
    <Container>
      <h1>HTTP Dog Search</h1>
      <SearchInput
        type="text"
        placeholder="Search HTTP codes (e.g., 4xx, 40x, 404)"
        value={search}
        onChange={(e) => setSearch(e.target.value)}
      />
      <ImageGrid>
        {filteredCodes.map((code) => (
          <Card key={code} code={code} />
        ))}
      </ImageGrid>
    </Container>
  );
};

export default Home;
