import React, { useEffect } from "react";
import { useListsContext } from "../hooks/useListsContext";
import { useNavigate } from "react-router-dom";

const Lists = () => {
  const { lists, dispatch } = useListsContext();
  const navigate = useNavigate();

  useEffect(() => {
    const fetchLists = async () => {
      const response = await fetch("/api/lists");
      const data = await response.json();
      if (response.ok) {
        dispatch({ type: "SET_LISTS", payload: data });
      }
    };
    fetchLists();
  }, [dispatch]);

  const handleDelete = async (id) => {
    const response = await fetch(`/api/lists/${id}`, { method: "DELETE" });

    if (response.ok) {
      dispatch({ type: "DELETE_LIST", payload: id });
    }
  };

  return (
    <div>
      <h2>Saved Lists</h2>
      {lists.map((list) => (
        <div
          key={list._id}
          style={{ borderBottom: "1px solid gray", padding: "10px" }}
        >
          <h3>{list.name}</h3>
          <p>Codes: {list.codes.join(", ")}</p>
          <button onClick={() => navigate(`/lists/${list._id}`)}>Edit</button>
          <button onClick={() => handleDelete(list._id)}>Delete</button>
        </div>
      ))}
    </div>
  );
};

export default Lists;
