import React, { useEffect } from "react";
import styled from "styled-components";
import { useListsContext } from "../hooks/useListContext";
import { useAuthContext } from "../hooks/useAuthContext"; // Import auth context
import { useNavigate } from "react-router-dom";
import Card from "./Card"; // Import the Card component

const Container = styled.div`
  max-width: 800px;
  margin: 0 auto;
  padding: 20px;
`;

const ListContainer = styled.div`
  background: #fff;
  padding: 20px;
  margin-bottom: 20px;
  border-radius: 8px;
  box-shadow: 2px 2px 10px rgba(0, 0, 0, 0.1);
  text-align: center;
`;

const ListTitle = styled.h2`
  color: #1aac83;
  font-size: 24px;
  margin-bottom: 10px;
`;

const CreatedAt = styled.p`
  font-size: 14px;
  color: #555;
  margin-bottom: 15px;
`;

const ImageGrid = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 10px;
  justify-content: center;
  margin-bottom: 15px;
`;

const ActionButtons = styled.div`
  display: flex;
  justify-content: center;
  gap: 15px;
`;

const Button = styled.button`
  background-color: ${(props) => (props.delete ? "#e7195a" : "#007bff")};
  color: white;
  padding: 10px 15px;
  border: none;
  font-size: 16px;
  cursor: pointer;
  border-radius: 5px;

  &:hover {
    background-color: ${(props) => (props.delete ? "#c4164a" : "#0056b3")};
  }
`;

const Lists = () => {
  const { lists, dispatch } = useListsContext();
  const { user } = useAuthContext(); // Get user from auth context
  const navigate = useNavigate();

  useEffect(() => {
    const fetchLists = async () => {
      if (!user) return; // Ensure user is logged in

      const response = await fetch("/api/lists", {
        headers: {
          Authorization: `Bearer ${user.token}`, // Include token
        },
      });

      const data = await response.json();
      if (response.ok) {
        dispatch({ type: "SET_LISTS", payload: data });
      }
    };

    fetchLists();
  }, [dispatch, user]);

  const handleDelete = async (id) => {
    if (!user) return;

    const response = await fetch(`/api/lists/${id}`, {
      method: "DELETE",
      headers: {
        Authorization: `Bearer ${user.token}`, // Include token
      },
    });

    if (response.ok) {
      dispatch({ type: "DELETE_LIST", payload: id });
    }
  };

  return (
    <Container>
      <h1>Saved Lists</h1>
      {lists.map((list) => (
        <ListContainer key={list._id}>
          <ListTitle>{list.name}</ListTitle>
          <CreatedAt>
            Created on: {new Date(list.createdAt).toLocaleString()}
          </CreatedAt>

          <ImageGrid>
            {list.codes.map((code) => (
              <Card key={code} code={code} />
            ))}
          </ImageGrid>

          <ActionButtons>
            <Button onClick={() => navigate(`/lists/${list._id}`)}>Edit</Button>
            <Button delete onClick={() => handleDelete(list._id)}>
              Delete
            </Button>
          </ActionButtons>
        </ListContainer>
      ))}
    </Container>
  );
};

export default Lists;
