import React, { useEffect } from "react";
import { useListsContext } from "../hooks/useListContext";
import { useAuthContext } from "../hooks/useAuthContext";
import { useNavigate } from "react-router-dom";
import styled from "styled-components";
import Card from "./Card"; // Import the Card component

const ListContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 20px;
  padding: 20px;
`;

const ListItem = styled.div`
  background: #fff;
  padding: 20px;
  border-radius: 8px;
  box-shadow: 2px 2px 10px rgba(0, 0, 0, 0.1);
  width: 80%;
  max-width: 600px;
  text-align: center;
`;

const ListName = styled.h3`
  font-size: 1.5rem;
  font-weight: bold;
  color: #1aac83;
  margin-bottom: 10px;
`;

const DateText = styled.p`
  font-size: 0.9rem;
  color: #666;
  margin-bottom: 15px;
`;

const ImageGrid = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 10px;
  justify-content: center;
  margin-bottom: 15px;
`;

const ButtonContainer = styled.div`
  display: flex;
  justify-content: center;
  gap: 10px;
`;

const ActionButton = styled.button`
  background-color: ${({ color }) => color || "#007bff"};
  color: white;
  padding: 8px 15px;
  border: none;
  cursor: pointer;
  font-size: 14px;
  border-radius: 5px;
  transition: background 0.3s;

  &:hover {
    background-color: ${({ hoverColor }) => hoverColor || "#0056b3"};
  }
`;

const EmptyMessage = styled.p`
  font-size: 1.2rem;
  color: #888;
  font-style: italic;
`;

const Lists = () => {
  const { lists, dispatch } = useListsContext();
  const { user } = useAuthContext();
  const navigate = useNavigate();

  useEffect(() => {
    const fetchLists = async () => {
      if (!user) return;

      const response = await fetch("/api/lists", {
        headers: {
          Authorization: `Bearer ${user.token}`,
        },
      });

      const data = await response.json();
      if (response.ok) {
        dispatch({ type: "SET_LISTS", payload: data });
      }
    };

    fetchLists();
  }, [dispatch, user]);

  const handleDelete = async (id) => {
    if (!user) return;

    const response = await fetch(`/api/lists/${id}`, {
      method: "DELETE",
      headers: {
        Authorization: `Bearer ${user.token}`,
      },
    });

    if (response.ok) {
      dispatch({ type: "DELETE_LIST", payload: id });
    }
  };

  return (
    <ListContainer>
      <h2>Saved Lists</h2>

      {lists.length === 0 ? (
        <EmptyMessage>No saved lists</EmptyMessage>
      ) : (
        lists.map((list) => (
          <ListItem key={list._id}>
            <ListName>{list.name}</ListName>
            <DateText>
              Created on: {new Date(list.createdAt).toLocaleString()}
            </DateText>

            <ImageGrid>
              {list.codes.map((code) => (
                <Card key={code} code={code} />
              ))}
            </ImageGrid>

            <ButtonContainer>
              <ActionButton onClick={() => navigate(`/lists/${list._id}`)}>
                Edit
              </ActionButton>
              <ActionButton
                color="#dc3545"
                hoverColor="#b02a37"
                onClick={() => handleDelete(list._id)}
              >
                Delete
              </ActionButton>
            </ButtonContainer>
          </ListItem>
        ))
      )}
    </ListContainer>
  );
};

export default Lists;
